# state codes
f_state_code <- read_csv("data/raw/states2.csv")

# wrangle ----
f_unemployment <- 
  f_unemployment %>%
  pivot_longer(cols = ends_with("UR"),
               names_to = "state",
               values_to = "unemployment") %>%
  mutate(state = str_sub(state, 1, 2)) %>% 
  rename(date = DATE) %>%
  left_join(f_state_code, by = c("state" = "Postal Code")) %>%
  mutate(state = Name) %>%
  select(-Name, -FIPS)

# write ----

write_csv(f_unemployment,
          file = "data/tidy/unemployment.csv")
